import React, {useState, useEffect} from 'react'
import AttributeButton from './AttributeButton'
import FilterList from './FilterList'

const FiltersBar = ({ setAttribute, setSecondaryFilter }) => {
    const [filterString, setFilterString] = useState("")
    const [operatorString, setOperatorString] = useState("")
    const [operatorFunction, setOperatorFunction] = useState(null)
    const [value, setValue] = useState(0)
    const [filter, setFilter] = useState(null)
    //#region Primary Filters
    const attrF = (hero, s) => {
        return hero.attribute!==s;
    }

    const atckF = (hero, s) => {
        return hero.atck_type!==s;
    }

    const sexF = (hero, s) => {
        return hero.sex!==s;
    }
    
    const strF = function(hero) {return attrF(hero, "STR")};
    const agiF = function(hero) {return attrF(hero, "AGI")};
    const intF = function(hero) {return attrF(hero, "INT")};

    const meleeF = function(hero) {return atckF(hero, "Melee")}
    const rangeF = function(hero) {return atckF(hero, "Ranged")}

    const maleF = function(hero) {return sexF(hero, "M")}
    const femaleF = function(hero) {return sexF(hero, "F")}
    const noneF = function(hero) {return sexF(hero, "N")}

    //#endregion Primary Filters
    //#region Secondary Filters
        const getOperatorFunction = (operatorString) => {
            switch(operatorString)
            {
                case "greaterthan":
                    setOperatorFunction(greaterthan);
                    break;
                case "lessthan":
                    setOperatorFunction(lessthan);
                    break;
                case "equalto":
                    setOperatorFunction(equalto);
                    break;
                case "greaterthanequalto":
                    setOperatorFunction(greaterthanequalto);
                    break;
                case "lessthanequalto":
                    setOperatorFunction(lessthanequalto);
                    break;
                default:
                    setOperatorFunction(null);
                    break;
            }
        }

        const getFilterFunction = (filterString) => {
            switch(filterString) {
                case "Starting Strength":
                    break;
                case "Starting Agility":
                    break;
                case "Starting Intelligence":
                    break;
                case "Strength Gain":
                    break;
                case "Agility Gain":
                    break;
                case "Intelligence Gain":
                    break;
                case "Move Speed":
                    break;
                case "Base HP":
                    break;
                case "Base Mana":
                    break;
                case "HP Regen":
                    break;
                case "Mana Regen":
                    break;
                case "Base Armor":
                    break;
                case "Base Attack Time":
                    break;
                case "Attack Range":
                    break;
                case "Projectile Speed":
                    break;
                default:
                    break;
            }
        }
        const greaterthan = (a, b) => {return a > b};
        const lessthan = (a, b) => {return a < b};
        const equalto = (a, b) => {return a===b};
        const greaterthanequalto = (a, b) => {return a >= b};
        const lessthanequalto = (a, b) => {return a <= b};

        const armorF = (hero, val) => {return operatorFunction(hero.armor, val)};
        const strStart = (hero, val) => {return operatorFunction(hero.str_strt, val)};
        const strGain = (hero, val) => {return operatorFunction(hero.str_gain, val)};
        const agiStart = (hero, val) => {return operatorFunction(hero.agi_strt, val)};
        const agiGain = (hero, val) => {return operatorFunction(hero.agi_gain, val)};
        const intStart = (hero, val) => {return operatorFunction(hero.int_strt, val)};
        const intGain = (hero, val) => {return operatorFunction(hero.int_gain, val)};
        const baseHP = (hero, val) => {return operatorFunction(hero.base_hp, val)};
        const hpRegen = (hero, val) => {return operatorFunction(hero.hp_regen, val)};
        const baseMana = (hero, val) => {return operatorFunction(hero.base_mana, val)};
        const manaRegen = (hero, val) => {return operatorFunction(hero.mana_regen, val)};
        const moveSpeed = (hero, val) => {return operatorFunction(hero.move_spd, val)};
        const bat = (hero, val) => {return operatorFunction(hero.bat, val)};
        const projSpeed = (hero, val) => {return operatorFunction(hero.projectile_spd, val)};
        const attackRange = (hero, val) => {return operatorFunction(hero.atck_range, val)};

    //#endregion Secondary FIlters

    useEffect(() => {
        setOperatorFunction(getOperatorFunction(operatorString));
    }, [operatorString])

    useEffect(() => {
        setFilter(getFilterFunction(filterString));
    }, [filterString])

    useEffect(() => {
        setSecondaryFilter(filter);
    }, [filter])

    return (
        <div className="filter_bar">
            <div className="filter_bar_name_container">Filters</div>
            <div className="filters">
                <div className="dicrete_categories">
                    <div className="attributes_filter_container">
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/hero_strength.png" filter={strF} setAttribute={setAttribute}/>
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/hero_agility.png" filter={agiF} setAttribute={setAttribute}/>
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/hero_intelligence.png" filter={intF} setAttribute={setAttribute}/>
                    </div>
                    <div className="attack_style_filter_container">
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/melee.svg" filter={meleeF} setAttribute={setAttribute}/>
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/ranged.svg" filter={rangeF} setAttribute={setAttribute}/>
                    </div>
                    <div className="sex_filter_container">
                        <AttributeButton filter={maleF} setAttribute={setAttribute}/>
                        <AttributeButton filter={femaleF} setAttribute={setAttribute}/>
                        <AttributeButton filter={noneF} setAttribute={setAttribute}/>
                    </div>
                </div>
                <div className="continuous_categories">
                    <FilterList/>

                </div>
            </div>
        </div>
    )
}

export default FiltersBar
