import React, { useState } from 'react'
import AttributeList from './AttributeList'

const SecondaryFiltersMenu = ({filters, addFilter}) => {
    const [selectedAttr, setSelectedAttr] = useState("move_spd");
    const [selectedOp, setSelectedOp] = useState("greaterthan")
    const [value, setValue] = useState(0)
    

    const grtr = (hero) => {return hero[selectedAttr] > value}
    const less = (hero) => {return hero[selectedAttr] < value}
    const eql = (hero) => {return hero[selectedAttr] === value}
    const grtrEql = (hero) => {return hero[selectedAttr] >= value}
    const lessEql = (hero) => {return hero[selectedAttr] <= value}

    const getFunc = (s) => {
        switch(s)
        {
            case "greaterthan": return grtr;
            case "lessthan": return less;
            case "equalto": return eql;
            case "greaterthanequalto": return grtrEql;
            case "lessthanequalto": return lessEql;
            default: return grtr;
        }
    } 
    return (
        <div className="filters_list_container">
            <AttributeList select={(s) => setSelectedAttr(s)} showName={false} />
            <div className="operator_and_input">
                <select className="operators_list" name="operators" id="operators" onChange={(e) => setSelectedOp(e.target.value)}>
                    <option value="greaterthan">{'>'}</option>
                    <option value="lessthan">{'<'}</option>
                    <option value="equalto">{'=='}</option>
                    <option value="greaterthanequalto">{'>='}</option>
                    <option value="lessthanequalto">{'<='}</option>
                </select>

                <form>
                    <input className="value_input_field" type="text" id="value" name="value" onChange={(e) => setValue(e.target.value)}></input>
                </form>

                <div className="secondary_filter_submit" onClick={() => {addFilter({id: 1, filter: getFunc(selectedOp)})}}/>
            </div>
        </div>
    )
}

export default SecondaryFiltersMenu
