import React, { useState } from 'react'
import AbilityList from './AbilityList'

const HeroCard = ({ hero }) => {
    const [abilities, setAbilities] = useState([
        {
            icon: 'https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/abilities/slark_dark_pact.png',
            name: 'Dark Pact'
        },
        {
            icon: 'https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/abilities/slark_pounce.png',
            name: 'Pounce'
        },
        {
            icon: 'https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/abilities/slark_essence_shift.png',
            name: 'Essence Shift'
        },
        {
            icon: 'https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/abilities/slark_shadow_dance.png',
            name: 'Shadow Dance'
        }
    ])
    {/* Fetch abilities from the ability collection */}

    return (
        <div className="card">
            <div className="card_outer">
                <div className="name_container">
                    <div className="hero_name">Slark</div>
                </div>
                <div className="card_inner">
                    <div className="hero_image_container">
                        {/* <img className="hero_image" src="https://cdn.cloudflare.steamstatic.com/apps/dota2/videos/dota_react/heroes/renders/slark.png" alt="slark.png" /> */}
                        <img className="hero_image" src="https://cdn.cloudflare.steamstatic.com/apps/dota2/videos/dota_react/heroes/renders/slark.png" alt="slark.png" />
                    </div>
                </div>
                {/*Map an array of abilities to create the ability bar*/}
                <AbilityList  abilities={abilities} />
            </div>
        </div>
    )
}

export default HeroCard
