import React, {useState, useEffect} from 'react'
import AttributeButton from './AttributeButton'
import AttributeList from './AttributeList'
import SecondaryFilter from './SecondaryFilter'
import SecondaryFiltersMenu from './SecondaryFiltersMenu'

const FiltersBar = ({ setAttribute, addSecondaryFilter, delSecondaryFilter, setOrderBy, getLimit, setAscending, secondaryFilters }) => {
    const [filterString, setFilterString] = useState("")
    const [operatorString, setOperatorString] = useState("")
    const [operatorFunction, setOperatorFunction] = useState(null)
    const [value, setValue] = useState(0)
    const [filter, setFilter] = useState(null)
    const [limit, setLimit] = useState(122);

    useEffect(() => {
        getLimit(limit);
    }, [limit])

    //#region Primary Filters
    const attrF = (hero, s) => {
        return hero.attribute!==s;
    }

    const atckF = (hero, s) => {
        return hero.atck_type!==s;
    }

    const sexF = (hero, s) => {
        return hero.sex!==s;
    }
    
    const strF = function(hero) {return attrF(hero, "STR")};
    const agiF = function(hero) {return attrF(hero, "AGI")};
    const intF = function(hero) {return attrF(hero, "INT")};

    const meleeF = function(hero) {return atckF(hero, "Melee")}
    const rangeF = function(hero) {return atckF(hero, "Ranged")}

    const maleF = function(hero) {return sexF(hero, "M")}
    const femaleF = function(hero) {return sexF(hero, "F")}
    const noneF = function(hero) {return sexF(hero, "N")}

    //#endregion Primary Filters
    //#region Secondary Filters

    const onLimitChange = (v) => {
        let result = v.replace(/[^0-9]/, "");
        result=(result>122)?122:result;
        setLimit(result)
    }



    return (
        <div className="filter_bar">
            <div className="filter_bar_name_container">Filters</div>
            <div className="filters">
                <div className="dicrete_categories">
                    <div className="attributes_filter_container">
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/hero_strength.png" filter={strF} setAttribute={setAttribute}/>
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/hero_agility.png" filter={agiF} setAttribute={setAttribute}/>
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/hero_intelligence.png" filter={intF} setAttribute={setAttribute}/>
                    </div>
                    <div className="attack_style_filter_container">
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/melee.svg" filter={meleeF} setAttribute={setAttribute}/>
                        <AttributeButton icon="https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/icons/ranged.svg" filter={rangeF} setAttribute={setAttribute}/>
                    </div>
                    <div className="sex_filter_container">
                        <AttributeButton filter={maleF} setAttribute={setAttribute}/>
                        <AttributeButton filter={femaleF} setAttribute={setAttribute}/>
                        <AttributeButton filter={noneF} setAttribute={setAttribute}/>
                    </div>
                </div>
                <div className="continuous_categories">
                    <SecondaryFiltersMenu filters={secondaryFilters} addFilter={addSecondaryFilter}/>
                    <div className="secondary_filter_list">
                        {secondaryFilters.map((sf) => <SecondaryFilter delFilter={delSecondaryFilter} i={sf.id}/>)}
                    </div>
                </div>
                <div className="sortby">
                    <AttributeList select={(s) => setOrderBy(s)} showName={true}/>
                    <div className="limit_container">
                        <label htmlFor="limittext">Limit:</label>
                        <input 
                            id="limittext" 
                            type="text" 
                            value={limit} 
                            onChange={(e) => onLimitChange(e.target.value)}
                            maxLength="3"
                        />
                    </div>
                    <label htmlFor="ascending_button">Ascending</label>
                    <input className="radio" type="radio" id="ascending_button" name="sort" value="asc" onClick={() => setAscending(1)} defaultChecked="true"></input>
                    <label htmlFor="descending_button">Descending</label>
                    <input className="radio" type="radio" id="descending_button" name="sort" value="dsc" onClick={() => setAscending(-1)}></input>
                </div>
            </div>
        </div>
    )
}

export default FiltersBar
