import './App.css';
import { useEffect, useState } from 'react'
import HeroCard from './Components/HeroCard';
import SearchBox from './Components/SearchBox';
import CardList from './Components/CardList';

function App() {
  const img_url = "https://cdn.cloudflare.steamstatic.com/apps/dota2/images/dota_react/heroes/"
  const hero_name = "slark"
  const ext =".png"

  const[heroes, setHeroes] = useState([])
  const[items, setItems] = useState([])
  const[isLoading, setIsLoading] = useState(true)
  const[query, setQuery] = useState("")

  //On start, get all heroes from the database
  useEffect(() => {
    const fetchHeroes = async () => {
      const result = await fetch('http://localhost:5000/')

      setHeroes(result.data)
      setItems(heroes)
      setIsLoading(false)
    }

    fetchHeroes()}, 
    [])

  return (
    <div className="App">
      <div className="hero_card_page">
        <SearchBox getQuery={(q) => setQuery(q) }/>
        <CardList className = "card_list" heroes={heroes} />
      </div>
    </div>
  );
}

export default App;
