import React from 'react'
import HeroCard from './HeroCard'
import ShiftButton from './ShiftButton'

const CardList = ({ heroes, pos, adjustPos }) => {

    const cardWidth = document.getElementsByClassName("card").style.width + document.getElementsByClassName(".card_container").style.marginRight * 2;

    return (
        <div className="hero_tape">
            <div id="button_container_left">
                <ShiftButton adjustPos={i => adjustPos(i)} addVal={-1}/>
            </div>
            <div id="button_container_right">
                <ShiftButton adjustPos={i => adjustPos(i)} addVal={1}/>
            </div>
            <div className="hero_list">
                {heroes.map((hero) => <HeroCard hero = {hero} />) }
            </div>
        </div>
    )
}

export default CardList
